from helpers.additive_elgamal import AdditiveElGamal
from charm.toolbox.integergroup import IntegerGroupQ, integer


p = integer()
q = integer()
elgamal_group = IntegerGroupQ()
elgamal = AdditiveElGamal(elgamal_group, p, q)

(pk, sk) = elgamal.keygen()

r = elgamal_group.random()

c = elgamal.encrypt(pk, 23, r)


print(c)

x = elgamal.decrypt(pk, sk, c)
print(x)